mapper.select:
select seq, title, sub_title, num, code, contents, write_date, viewcount, thumbnail_image
from (select rownum r, seq, title, sub_title, num, code, contents, write_date, viewcount, thumbnail_image
	  from (select * from pro_board order by write_date asc))	
where r between

	#{r}

select lpad(code, 8,0) code, num, name, price, sizes, stock, color, cate_code
from (select rownum r, lpad(code, 8,0) code, num, name, price, sizes, stock, color, cate_code
	  from (select * from pro where cate_code=#{cate_code} order by code asc))	
where r between

	#{r}

mapper.update:
update pro set name=#{name}, price=#{price}, sizes=#{sizes},
sal_rate=#{sal_rate}, stock=#{stock}, color=#{color}, cate_code=#{cate_code}
where code=#{code}

update pro_board set title=#{title}, sub_title=#{sub_title}, num=#{num},
contents=#{contents}, thumnail_image=#{thumnail_image}
where code=#{code}
